/* eslint no-unused-vars: 1 */

import React, { useCallback, useState } from 'react';

const ShortenUrlForm = () => {
    const [value, setValue] = useState('');

    const onChange = useCallback(
        (e) => {
            setValue(e.target.value)
        },
        [
            /* TODO: Add necessary deps */
        ],
    );

    const onSubmit = useCallback(
        (e) => {
            e.preventDefault();
            const urlJson = {value};

            fetch('http://127.0.0.1:8000/shorturl/', {
                method: 'POST', 
                headers: {"Content-Type": "application/json", "Access-Control-Allow-Origin": "*"},
                body: JSON.stringify(urlJson)
            }).then(() => {
                console.log("new blog added")
            })
        },
        [
            /* TODO: necessary deps */
        ],
    );

    return (
        <form onSubmit={onSubmit}>
            <label htmlFor="shorten">
                Url:
                <input
                    placeholder="Url to shorten"
                    id="shorten"
                    type="text"
                    value={value}
                    onChange={onChange}
                />
            </label>
            <input type="submit" value="Shorten and copy URL" />
            {/* TODO: show below only when the url has been shortened and copied */}
            <div>{/* Show shortened url --- copied! */}</div>
        </form>
    );
};

export default ShortenUrlForm;
